INSERT INTO `course_base`(`id`,`name`,`users`,`mt`,`grade`,`studymodel`,`teachmode`,`description`,`st`,`status`,`create_time`,`is_deleted`,`serialize`,`subtitle`,`target`,`study_num`) VALUES (1,'一站式学习Redis  从入门到高可用分布式实践','',5,'200002','201001',NULL,'一门内容非常丰富的Redis课程，基于原Redis课程进行升级。由阿里云Redis开发规范原作者为你深入讲解每个技术点。课程包含Redis基础，使用经验介绍、Java，Python客户端示范、Redis使用规范，由浅入深讲解并伴有企业中Redis开发的相关项目以及大规模Redis的实战经验，是开发和运维人员系统学习Redis的必备内容',298,'202002','2019-08-13 06:02:22.089172',0,'205002','一门内容丰富的Redis课程','掌握Redis的基础使用和集群搭建与管理',233),(5,'数据科学人工智能数学基础','',5,'200001','201001',NULL,'本课程旨在帮助有意向学习人工智能或数据科学方向课程的同学快速打下数学基础。课程内容涉及高等数学，线性代数，概率论与统计学，在学习过程中应当以理解为出发点并不需要死记每一个公式，快速掌握核心知识内容。',298,'202002','2019-08-13 06:15:02.224414',0,'205002',NULL,NULL,14),(8,'零基础7天精通kettle8.2','',5,'200001','201001',NULL,'ETL工程师，在数据仓库类职位中占有很大比例，而且薪水都不差。IT人一下子转型数据分析师可能跳跃度比较大难度高，先转型数据仓库/ETL工程师再择机往更高处走是合理选择之一。在ETL软件中，使用最多的是开源的Kettle，完全免费，功能和性能不弱于datastage这类商业ETL软件，使用Kettle和其它开源数据平台软件，例如Mysql集群，Hadoop集群等组合在一起，是性价比极高的架构选择。',298,'202002','2019-08-13 09:51:23.837768',0,'205002',NULL,NULL,223),(9,'新媒体社群运营从0到1快速入门','',5,'200001','201001',NULL,'本课程包含社群运营7大必备模块，社群定义、如何快速建立优秀社群、目前主流社群涨粉秘籍、提升社群活跃度的套路、提升社群用户转化率必杀技，再到爆款社群案例剖析，还有社群运营面试宝典，给学员一套上百万用户的社群拉新、促活、留存转化模型。本课程注重理论和实操相结合，案例拆解和知识梳理并行。学完本课程后，0基础学员快速入门，并具备胜任相应工作岗位的能力。',298,'202002','2019-08-13 09:51:27.180166',0,'205002',NULL,NULL,59),(10,'游戏AI实战课—深入浅出tensorflow.js','',5,'200001','201001',NULL,'本课程是特别针对人工智能感兴趣的零基础学习者研发的快速入门级课程，本着以最深入浅出的讲解形式、最通俗易懂的内容设置、最有趣丰富的游戏案例安排为教学宗旨，带领学习者轻松踏入人工智能的神秘领域。',298,'202002','2019-08-13 09:51:30.238540',0,'205002',NULL,NULL,105),(11,'淘系电商实战运营课程','',5,'200001','201001',NULL,'本课程包含从零基础到高端玩法全系列淘系内容，使学员完成从电商小白到电商大咖的华丽转身！',298,'202002','2019-08-13 09:51:35.955315',0,'205002',NULL,NULL,55),(12,'跨境电商实战课程','',5,'200001','201001',NULL,'系统化亚马逊运营课程，深度解析亚马逊电商平台的规则及运营技巧，帮助学员轻松赚美金！',298,'202002','2019-08-13 09:51:38.092006',0,'205002',NULL,NULL,60),(13,'京东电商实战课程','',5,'200001','201001',NULL,'具备实战性与实用性的京东运营课程，从0到1的系统化学习过程，全方位快速提升京东店铺运营能力。',298,'202002','2019-08-13 